import { connect, Provider } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import { PropTypes } from "prop-types";
import React, { Fragment, useState } from "react";
import {useSelector, useDispatch} from 'react-redux'
import LOGOUT from "../../reducers/auth";
import  { loadUser } from "../../actions/auth";
import {login} from "../../actions/auth";
import PrivateRoute, {isAuthenticated} from "../routing/PrivateRoute";
import { logout } from "../../actions/auth";
import initialState from "../../reducers/auth";
import combineReducers from "../../reducers/";
import store from "../../store";

export const Logout = ( ) => {  

const dispatch = useDispatch();
const Loggedin = useSelector(initialState => initialState.isAuthenticated);
const Loggedout = useSelector(initialState => initialState.isAuthenticated);

const onlogoutClick = async e => {
    e.preventDefault();
    localStorage.clear();
    window.location.href = '/';  
    
  };  

  return (
    <Provider store={store}> 
    <div> 
      { isAuthenticated ? 
      ( <button type="button" class="logout btn btn-primary" onClick={() => dispatch(combineReducers.auth.logout())}>
      Log Out
      </button> 
       ) : ( 
      <button type="button" class="logout btn btn-primary" onClick={e => onlogoutClick(e)}>
      Log AAAAAAA
      </button> )}
    </div>
    </Provider>
   
     
  )};
      
  Logout.propTypes = {
    logout: PropTypes.func.isRequired,
    isAuthenticated: PropTypes.bool
  };
  
  const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
  });
  
  export default connect(
    mapStateToProps,
    { logout }
  )(Logout);
