import {
    // GET_REPOS,
    GET_PROFILE,
    GET_PROFILES,
    UPDATE_PROFILE,
    PROFILE_ERROR,
    CLEAR_PROFILE
  } from "../actions/types";
  
  const initialState = {
    profile: null,
    profiles: [],
    loading: true,
    error: {}
  };
  
  export default function(state = initialState, action) {
    const { type, payload } = action;
  
    switch (type) {
      case GET_PROFILE:
      case UPDATE_PROFILE:
        return {
          state,
          profile: payload,
          loading: false
        };
  
      case GET_PROFILES:
        return {
          ...state,
          profiles: payload,
          loading: false
        };
  
      case PROFILE_ERROR:
        return {
          ...state,
          error: payload,
          loading: false
        };
  
      case CLEAR_PROFILE:
        return {
          ...state,
          profile: null,
          repos: [],
          loading: false
        };
  
    //   case GET_REPOS:
    //     return {
    //       ...state,
    //       repos: payload,
    //       loading: false
    //     };
  
      default:
        return state;
    }
  };
